var remote = require('./gzhelpers.js').remote;

var options = {
  hostname: 'localhost',
  port: 3000,
  path: '/hello',
  method: 'POST',
};

console.log('A web server should be running on localhost:3000');

remote.request(options, 'hello world').then( console.log.bind(console));
remote.request(options, 'foo bar').then( console.log.bind(console));
remote.request(options, {foo: 'bar'}).then( console.log.bind(console));
